function Answer({ label, input, output }) {
  return (
    <Box bg="#fff" p={15} mt={5} borderRadius={6}>
      <Heading color="#00AC97" as="h3" size="lg" >{label}</Heading>
      <Box>
      <Button position="absolute" top={5} right={5} >d</Button>
        <Box>Вопрос: {input}</Box>
        <Box>Ответ: {output}</Box>
      </Box>
    </Box>
  );
}

export default Answer;
