import { SearchIcon } from "@chakra-ui/icons";
import {
  Box,
  Card,
  Heading,
  Input,
  InputGroup,
  InputRightElement,
  Select,
} from "@chakra-ui/react";
import { AppState } from "../../context";

function DirectionsPage() {
  const unvercities = AppState().unvercities;

  return (
    <Box
      w="100%"
      h="100vh"
      display="flex"
      flexDir="column"
      alignItems="center"
      p={10}
      background="gray.100"
    >
      <Heading as="h2" size="2xl">
        Направления нашего университета
      </Heading>
      <Box display="flex" justifyContent="space-between">
        <Card mt={5} size="lg">
          <InputGroup>
            <Input placeholder="Поиск" _placeholder={{ color: "#00AC97" }} />
            <InputRightElement children={<SearchIcon color="#00AC97" />} />
          </InputGroup>
        </Card>
        <Card>
          {unvercities.length && (
            <Select>
              {unvercities.map((univer, i) => (
                <option key={i} value={univer}>
                  {univer}
                </option>
              ))}
            </Select>
          )}
        </Card>
      </Box>
    </Box>
  );
}

export default DirectionsPage;
