import { DeleteIcon } from "@chakra-ui/icons";
import {
  Box,
  Button,
  ButtonGroup,
  Heading,
  IconButton,
} from "@chakra-ui/react";

function Answer({ label, input, output }) {
  return (
    <Box bg="#fff" p={15} mt={5} borderRadius={6} position="relative">
      <ButtonGroup>
        <IconButton
          position="absolute"
          top={5}
          right={5}
          pl="7px"
          leftIcon={<DeleteIcon />}
        />
      </ButtonGroup>
      <Heading color="#00AC97" as="h3" size="lg">
        {label}
      </Heading>
      <Box>
        <Box>Вопрос: {input}</Box>
        <Box>Ответ: {output}</Box>
      </Box>
    </Box>
  );
}

export default Answer;
